 .fillMaxWidth()
 .padding(16.dp),
 shape = RoundedCornerShape(16.dp),
 elevation = CardDefaults.cardElevation(8.dp),
 colors = CardDefaults.cardColors(containerColor = Color(0xFFE0C1A6))
 ) {
 Column(
