 .align(Alignment.Start)
 .padding(bottom = 8.dp)
 )
 Text(
 text = "Cashier: ${accountHolder?.branch ?: "No User"}",
 color = Color.White.copy(alpha = 0.5f),
 modifier = Modifier
 .align(Alignment.Start)
 )

 Spacer(modifier = Modifier.height(80.dp))

 // Live Camera Feed
 Card(
 modifier = Modifier
 .fillMaxWidth()
 .padding(vertical = 8.dp),
