 ) {
 Text(
 text = "ENTER PIN",
 style = MaterialTheme.typography.titleLarge,
 color = Color.White,
 modifier = Modifier
 .padding(8.dp)
 .background(Color(0xFF8B4513), shape = RoundedCornerShape(8.dp))
 .padding(horizontal = 16.dp, vertical = 8.dp)
 )
 Spacer(modifier = Modifier.height(16.dp))
 OtpTextField(
