 fun verifyPin(pin: String) {
 val url = "http://192.168.254.107/CalleCafe/verify_pin.php"
 val requestQueue: RequestQueue = Volley.newRequestQueue(context)

 val stringRequest = object : StringRequest(
 Request.Method.POST, url,
 Response.Listener { response ->
 try {
 val jsonResponse = JSONObject(response)
 if (jsonResponse.getBoolean("success")) {
 // PIN verification successful
 loginSuccess = true
 } else {
 // PIN verification failed
 errorMessage = jsonResponse.getString("message")
 }
 } catch (e: Exception) {
 errorMessage = "Unexpected response from server"
 }
 },
 Response.ErrorListener {
 errorMessage = "Verification Failed! Please check your internet connection"
 }
 ) {
 override fun getParams(): MutableMap<String, String> {
 val params = HashMap<String, String>()
 params["pin"] = pin
 accountHolder?.let {
 params["cashierName"] = it.name
 params["branch"] = it.branch
 }
 return params
 }
 }

 requestQueue.add(stringRequest)
 }

