fun updateData(pin: String, username:String, cashierName: String, branch: String) {
 val url = "http://192.168.254.107/accounts.php"
 Log.d("NETWORK", "Attempting to POST to URL: $url") // Added URL logging

 val requestQueue: RequestQueue = Volley.newRequestQueue(context)

 val stringRequest = object : StringRequest(
 Request.Method.POST, url,
 Response.Listener { response ->
 Log.d("NETWORK", "Received response: $response") // Log raw response
 try {
 val jsonResponse = JSONObject(response)
 if (jsonResponse.getBoolean("success")) {
 Log.e("NETWORK", "Received unexpected success without hitting server!")
 Toast.makeText(
 context,
 "Your account's Information has been updated!",
 Toast.LENGTH_SHORT
 ).show()
 navController.navigate("Routes.ScannerScreen") {
 popUpTo("Routes.AccountsScreen") { inclusive = true }
 }
 } else {
 val message = jsonResponse.getString("message")
 Log.d("NETWORK", "Received failure response: $message")
 Toast.makeText(
 context,
 "Update Failed: $message",
 Toast.LENGTH_LONG
 ).show()
 }
 } catch (e: Exception) {
 Log.e("NETWORK", "Error parsing response: ${e.message}")
 Toast.makeText(
 context,
 "Invalid server response",
 Toast.LENGTH_LONG
 ).show()
 }
 },
 Response.ErrorListener { error ->
 Log.e("NETWORK", "Volley error: ${error.message}")
 Toast.makeText(
 context,
 "Network Error: ${error.message}",
 Toast.LENGTH_LONG
 ).show()
 }
 ) {
 override fun getParams(): MutableMap<String, String> {
 val params = HashMap<String, String>()
 params["pin"] = pin
 params["username"] = username
 accountHolder?.let { holder ->
 params["cashierName"] = holder.name
 params["branch"] = holder.branch
 Log.d("NETWORK_PARAMS", "Sending params: $params")
 }
 return params
 }

 override fun getHeaders(): MutableMap<String, String> {
 val headers = HashMap<String, String>()
 headers["Content-Type"] = "application/x-www-form-urlencoded"
 return headers
 }
 }

 Log.d("NETWORK", "Adding request to queue")
 requestQueue.add(stringRequest)
}

// ... rest of the existing code ...
